import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'
import { useState } from 'react';
import createUtilityClassName from 'react-bootstrap/esm/createUtilityClasses';
import Phase2 from './form/Phase2';
import Phase3 from './form/Phase3';

function Landing() {
  const genres=[{"id":28,"name":"Action"},{"id":12,"name":"Adventure"},{"id":16,"name":"Animation"},{"id":35,"name":"Comedy"},{"id":80,"name":"Crime"},{"id":99,"name":"Documentary"},{"id":18,"name":"Drama"},{"id":10751,"name":"Family"},{"id":14,"name":"Fantasy"},{"id":36,"name":"History"},{"id":27,"name":"Horror"},{"id":9648,"name":"Mystery"},{"id":10749,"name":"Romance"},{"id":878,"name":"Science Fiction"},{"id":53,"name":"Thriller"},{"id":10752,"name":"War"},{"id":37,"name":"Western"}]

  const [phase,setPhase]= useState("one")
  function upPhase(){
    if(phase=="one"){
      setPhase("two")
    }else{
      if(phase=="two"){
        setPhase("three")
      }
    }
  }


  return (
   

      phase=="one"?
      <div class="text-center w-full mx-auto py-12 px-4 sm:px-6 lg:py-16 lg:px-8 z-20 border-2 border-black">
        <h2 class= "text-black">
            <span class="block font-extrabold text-3xl">
                Let's find you a movie!
            </span>
            <span class="block text-slate-600	 text-xl font-bold">
              Do you have a genre in mind?
            </span>
        </h2>
       
        <div class="lg:mt-0 lg:flex-shrink-0">
            <div class="mt-12 flex-inline space-x-40 rounded-md border-2 border-slate-50">
              <button class="bg-green-500 hover:bg-green-400 text-white font-bold py-2 px-4 border-b-4 border-green-700 hover:border-green-500 rounded">
                Yes</button>
                <button class="bg-red-500 hover:bg-red-400 text-white font-bold py-2 px-4 border-b-4 border-red-700 hover:border-red-500 rounded">
                 No
              </button>
              <button class=" hover:bg-green-400 text-black font-bold rounded">
              <svg xmlns="http://www.w3.org/2000/svg" fill="pink" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
  <path stroke-linecap="round" stroke-linejoin="round" d="M6.633 10.5c.806 0 1.533-.446 2.031-1.08a9.041 9.041 0 012.861-2.4c.723-.384 1.35-.956 1.653-1.715a4.498 4.498 0 00.322-1.672V3a.75.75 0 01.75-.75A2.25 2.25 0 0116.5 4.5c0 1.152-.26 2.243-.723 3.218-.266.558.107 1.282.725 1.282h3.126c1.026 0 1.945.694 2.054 1.715.045.422.068.85.068 1.285a11.95 11.95 0 01-2.649 7.521c-.388.482-.987.729-1.605.729H13.48c-.483 0-.964-.078-1.423-.23l-3.114-1.04a4.501 4.501 0 00-1.423-.23H5.904M14.25 9h2.25M5.904 18.75c.083.205.173.405.27.602.197.4-.078.898-.523.898h-.908c-.889 0-1.713-.518-1.972-1.368a12 12 0 01-.521-3.507c0-1.553.295-3.036.831-4.398C3.387 10.203 4.167 9.75 5 9.75h1.053c.472 0 .745.556.5.96a8.958 8.958 0 00-1.302 4.665c0 1.194.232 2.333.654 3.375z" />
</svg>


              </button>
              



            </div>
            {/* <div class="mt-10">
              <h2 class= "text-black">
              <span class="block font-extrabold text-3xl">
                  Select a genre 
              </span>  
             </h2>
           <div class=" flex-auto flex-wrap space-x-12 space-y-12">
            {genres.map(ele=>
              <button onClick={upPhase} class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                {ele["name"]}
              </button>)}
            </div>
        </div> */}
        </div>
    </div>:phase=="two"? <Phase2/> : <Phase3/>
  )
}

export default Landing
