import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
import Card from "./Card"
function List() {
    const [list,setList]=useState(null)
   async function getList(){
    const fetchedList = await axios.get("http://localhost:8000/my-list").then(res=>{console.log(res);setList(res.data)})
    }
     async function deleteList(id){
      
        const deleteList = await axios.post("http://localhost:8000/delete", {id:id}).then(res=>setList(''))
    }
useEffect(()=>{
    getList()
},[])

  return (
    <>
    <h1>List</h1>
    {list ?
       list.map(ele=><Card info={ele}/>)
    : "loading"}
    
   
   </>

  )
}

export default List
