import React, { useState} from 'react'
import {useNavigate} from 'react-router-dom'
import axios from 'axios'
import jwt_decode from 'jwt-decode'


function Login() {
    const[data,setData]=useState({user:'Hasanali',password:'123123'});
    const navigator= useNavigate();


  async  function handleSubmit(e){
    e.preventDefault();
        const url="http://localhost:8000/login"
        // const {data:res}= await axios.post(url,{user:'Hasanali',password:'123123'});
        await axios.post(url,{user:'Hasanali',password:'123123'})
        .then(res => {
          if (res.status == 200) {
            localStorage.setItem('token', res.data)
          }  else {
            console.log('invalid credentials, user was not able to be logged in')
          }
        }).then(() => {
          debugger
          let token = localStorage.getItem('token')
          let user = jwt_decode(token)
          setData(user)

        })
        // console.log('res!', res)
        // debugger
        navigator("/landing")
}


  return (
    <>
    <div>Login</div>
    <form onSubmit={handleSubmit}>
        <input type="text" name="user"/>
        <input type="password" name="password"/>
        <input type="submit" value="submit"/>
    </form>
    </>
  )
}

export default Login
