import React,{useState,useEffect} from 'react'
import axios from 'axios'

function List() {
    const [list,setList]=useState(null)
    const [id,setId]=useState("")
   async function getList(){
    const fetchedList = await axios.get("http://localhost:8000/my-list").then(res=>{console.log(res);setList(res.data)})
    }
     async function updateList(){
        const fetchedList = await axios.get("http://localhost:8000/update")
    }
     async function deleteList(id){
        const fetchedList = await axios.get("http://localhost:8000/delete",{id:id})
    }
useEffect(()=>{
    getList()
},[])

  return (
    <>
    <h1>List</h1>
    {list ?
       list.map(ele=><p>{ele.filmName} <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full"onClick={()=>{deleteList(ele._id.valueOf())}}>
          Delete
     </button></p>)
    : "loading"}
    
   
   </>

  )
}

export default List
