import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import axios from 'axios'
function Movie(props) {
  const [movieParams,setMovieParams] =useState(props.movieParams)
  const [movie,setMovie]=useState('')
  const [count,setCount]=useState(0)
  async function getRec(req,res){
    const fetched_rec= await axios.get('https://api.themoviedb.org/3/discover/movie', {params: movieParams})
    setMovie(fetched_rec.data.results[count])
    setCount(count+1);
  }
  useEffect(()=>{
    getRec();
  },[])
  return (
    <>
    <div>Movie</div>
    <p>{movie.title}</p>
        <img src="https://image.tmdb.org/t/p/original/zt6sKnx9dEiRCb7oVMlfmmMGJMO.jpg" width={300} height={340} style={{backgroundSize: 'cover'}}/>
      </>
  )
}

export default Movie
