import React from 'react'
import axios from 'axios'
import { useState } from 'react';

function Landing() {
  const [movie,setMovie]=useState('')
  const [count,setCount]=useState(0)

  async function getRec(req,res){
    const fetche_rec= await axios.get('https://api.themoviedb.org/3/discover/movie', { params: { api_key:"f4a00265d0b6421a5949e48531d923f8",language:"en-US",include_adult:false, include_video:false,page:1,with_genres:"35",page:3} });
    setMovie(fetche_rec.data.results[count])
    setCount(count+1);
  }
  return (
   <>
      <p>Lets get a comedy movie</p>
      <button onClick={getRec}>Click me</button>
      <button onClick={getRec}>Find another</button>
     {count<20? <div>
        <p>{movie.title}</p>
        {/* <img src="https://image.tmdb.org/t/p/original/zt6sKnx9dEiRCb7oVMlfmmMGJMO.jpg" width={300} height={340} style={{        backgroundSize: 'cover', */}

      </div>:null}
   </>
  )
}

export default Landing
