import React,{useState,useEffect} from 'react'
import axios from 'axios'

function List() {
    const [list,setList]=useState(null)
   async function getList(){
    const fetchedList = axios.get("http://localhost:8000/my-list").then(res=>{console.log(res);setList(res.data)})
    }
useEffect(()=>{
    getList()
},[])
async function deleteItem(){
   await axios.delete("http://localhost:8000/my-list").then(res=>{
        if(res=="success"){
            getList();
        }
    })
}
  return (
    <>
    <h1>List</h1>
    {list ?
       list.map(ele=><p>{ele.filmName} <button onClick={deleteItem}>Delete</button></p>)
    : "loading"}
    
   
   </>

  )
}

export default List
